import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { FaRegHeart } from 'react-icons/fa';
import './index.css';

const Jobs = () => {
  const [jobs, setJobs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const jobsPerPage = 10;

  const fetchJobs = useCallback(async () => {
    try {
      const response = await fetch('https://api.jsonbin.io/v3/b/65f9c3c2266cfbbc3fdd8c0f');
      const data = await response.json();
      const jobsData = data.record.jobs || [];
      setJobs(jobsData);
      setTotalPages(Math.ceil(jobsData.length / jobsPerPage));
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  }, []);

  useEffect(() => {
    fetchJobs();
  }, [fetchJobs]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const indexOfLastJob = currentPage * jobsPerPage;
  const indexOfFirstJob = indexOfLastJob - jobsPerPage;
  const currentJobs = jobs.slice(indexOfFirstJob, indexOfLastJob);

  return (
    <div className="jobs-container">
      <div className="job-background">
        <div className="title">
          <h2>Find Your Dream Job</h2>
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Search jobs..."
            />
          </div>
        </div>
      </div>
      <div className="job-section">
        <div className="jobs-for-you">
          <div className="job-list">
            {currentJobs.map((job) => (
              <div key={job.id} className="job-card">
                <div className="job-header">
                  <img
                    src={job.companyLogo}
                    alt={job.company}
                    className="company-logo"
                  />
                  <div className="job-content">
                    <h3 className="job-title">{job.title}</h3>
                    <p className="company-name">{job.company}</p>
                    <p className="job-description">{job.description}</p>
                    <div className="job-actions">
                      <button className="save-button">
                        <FaRegHeart />
                      </button>
                      <Link to={`/job/${job.id}`} className="view-details">
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="pagination">
            <button 
              className="prev" 
              onClick={() => handlePageChange(currentPage - 1)} 
              disabled={currentPage === 1}
            >
              Previous
            </button>
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index + 1}
                onClick={() => handlePageChange(index + 1)}
                className={currentPage === index + 1 ? 'active' : ''}
              >
                {index + 1}
              </button>
            ))}
            <button 
              className="next" 
              onClick={() => handlePageChange(currentPage + 1)} 
              disabled={currentPage === totalPages}
            >
              Next
            </button>
          </div>
        </div>
        <div className="job-page">
          {/* Job filters and other content */}
        </div>
      </div>
    </div>
  );
};

export default Jobs;
