import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaRegHeart, FaHeart, FaMapMarkerAlt, FaBriefcase, FaMoneyBillWave, FaBuilding } from 'react-icons/fa';
import './index.css';

const JobDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(null);
  const [isSaved, setIsSaved] = useState(false);
  const [isApplied, setIsApplied] = useState(false);

  useEffect(() => {
    // Fetch job details
    const fetchJobDetails = async () => {
      try {
        const response = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`);
        if (!response.ok) throw new Error('Failed to fetch job details');
        const data = await response.json();

        // Transform the data to match our job structure
        setJob({
          id: data.id,
          title: data.title,
          company: `Company ${data.id}`,
          description: data.body,
          companyLogo: `https://via.placeholder.com/50?text=Co${data.id}`,
          location: 'Remote',
          salary: '$50,000 - $100,000',
          type: 'Full-time',
          requirements: [
            'Bachelor\'s degree in Computer Science or related field',
            '3+ years of experience in software development',
            'Strong problem-solving skills',
            'Excellent communication abilities'
          ],
          responsibilities: [
            'Develop and maintain web applications',
            'Collaborate with cross-functional teams',
            'Write clean, maintainable code',
            'Participate in code reviews'
          ]
        });

        // Check if job is saved
        const savedJobs = JSON.parse(localStorage.getItem('savedJobs') || '[]');
        setIsSaved(savedJobs.some(savedJob => savedJob.id === parseInt(id)));

        // Check if job is applied
        const appliedJobs = JSON.parse(localStorage.getItem('appliedJobs') || '[]');
        setIsApplied(appliedJobs.some(appliedJob => appliedJob.id === parseInt(id)));
      } catch (error) {
        console.error('Error fetching job details:', error);
      }
    };

    fetchJobDetails();
  }, [id]);

  const handleSaveJob = () => {
    const savedJobs = JSON.parse(localStorage.getItem('savedJobs') || '[]');
    if (!isSaved) {
      savedJobs.push(job);
      localStorage.setItem('savedJobs', JSON.stringify(savedJobs));
    } else {
      const newSavedJobs = savedJobs.filter(savedJob => savedJob.id !== job.id);
      localStorage.setItem('savedJobs', JSON.stringify(newSavedJobs));
    }
    setIsSaved(!isSaved);
  };

  const handleApply = () => {
    const appliedJobs = JSON.parse(localStorage.getItem('appliedJobs') || '[]');
    if (!isApplied) {
      appliedJobs.push(job);
      localStorage.setItem('appliedJobs', JSON.stringify(appliedJobs));
      setIsApplied(true);
    }
    navigate(`/apply/${id}`);
  };

  if (!job) return <div className="loading">Loading...</div>;

  return (
    <div className="job-details-container">
      <div className="job-details-header">
        <div className="job-details-content">
          <img src={job.companyLogo} alt={job.company} className="company-logo" />
          <div className="job-header-info">
            <h1>{job.title}</h1>
            <div className="company-info">
              <FaBuilding /> {job.company}
            </div>
            <div className="job-meta">
              <span className="job-location">
                <FaMapMarkerAlt /> {job.location}
              </span>
              <span className="job-salary">
                <FaMoneyBillWave /> {job.salary}
              </span>
              <span className="job-type">
                <FaBriefcase /> {job.type}
              </span>
            </div>
          </div>
          <div className="job-actions">
            <button 
              className={`save-button ${isSaved ? 'saved' : ''}`}
              onClick={handleSaveJob}
            >
              {isSaved ? <FaHeart /> : <FaRegHeart />}
            </button>
            <button 
              className={`apply-button ${isApplied ? 'applied' : ''}`}
              onClick={handleApply}
              disabled={isApplied}
            >
              {isApplied ? 'Applied' : 'Apply Now'}
            </button>
          </div>
        </div>
      </div>
      <div className="job-details-body">
        <div className="job-description">
          <h2>Job Description</h2>
          <p>{job.description}</p>
        </div>
        <div className="job-requirements">
          <h2>Requirements</h2>
          <ul>
            {job.requirements.map((req, index) => (
              <li key={index}>{req}</li>
            ))}
          </ul>
        </div>
        <div className="job-responsibilities">
          <h2>Responsibilities</h2>
          <ul>
            {job.responsibilities.map((resp, index) => (
              <li key={index}>{resp}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default JobDetails;
